import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Profile.css';

export default function Profile({ match }) {
    const [user, setUser] = useState(null); // To store user data
    const [error, setError] = useState(''); // To handle errors

    const userId = match.params.userId; // Get the userId from the URL (React Router)

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const res = await axios.get(`http://localhost:4000/profile/users/${userId}`);
                setUser(res.data); // Store the user data
            } catch (err) {
                console.error('Error fetching user profile:', err);
                setError('Failed to load user profile.');
            }
        };

        fetchUserData();
    }, [userId]);

    if (error) {
        return <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>;
    }

    if (!user) {
        return <div style={{ textAlign: 'center' }}>Loading profile...</div>;
    }

    return (
        <div className="profile-container">
            {/* Profile Header */}
            <header className="profile-header">
                <div className="profile-picture">ðŸ¦ˆ</div>
                <div className="profile-info">
                    <h1 className="profile-name">{user.username}</h1>
                    <p className="profile-bio">
                        {/* Add a bio field in the user schema if needed */}
                        {user.bio || 'This user has not added a bio yet.'}
                    </p>
                </div>
            </header>

            {/* Scrapbook Pages Grid */}
            <div className="scrapbook-grid">
                {user.scrapPages && user.scrapPages.length > 0 ? (
                    user.scrapPages.map((page) => (
                        <div key={page._id} className="scrapbook-item">
                            <div
                                style={{
                                    border: '1px solid #ccc',
                                    borderRadius: '10px',
                                    padding: '10px',
                                    backgroundColor: page.color || '#f9f9f9',
                                }}
                            >
                                <h3>{user.username}</h3>
                                <p>{page.description}</p>

                                {/* Render Images */}
                                {page.binaryImages && page.binaryImages.length > 0 ? (
                                    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(100px, 1fr))', gap: '10px' }}>
                                        {page.binaryImages.map((image, index) => (
                                            <img
                                                key={index}
                                                src={image}
                                                alt={`Scrapbook ${index}`}
                                                style={{ width: '150px', height: '150px', objectFit: 'cover' }}
                                            />
                                        ))}
                                    </div>
                                ) : (
                                    <p>No images available</p>
                                )}

                                {/* Tags */}
                                <p>
                                    {page.tags.map((tag, index) => (
                                        <span key={index}>#{tag} </span>
                                    ))}
                                </p>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>This user has not created any scrapbook pages yet.</p>
                )}
            </div>
        </div>
    );
}


