// import { Link } from 'react-router-dom';
// import React, { useState } from 'react';

import React, { useState } from 'react';

function ImageStickerUploader() {
  const [numImages, setNumImages] = useState(1); // Number of image slots (1 to 4)
  const [images, setImages] = useState([]); // Store uploaded images
  const [description, setDescription] = useState(''); // Store description
  const [stickers, setStickers] = useState([]); // Store sticker positions
  const [savedFrames, setSavedFrames] = useState([]); // Store saved frames for another page

  const handleNumImagesChange = (event) => {
    const newNumImages = parseInt(event.target.value, 10);
    setNumImages(newNumImages);
    setImages(Array(newNumImages).fill(null)); // Reset images
    setStickers([]); // Clear stickers
  };

  const handleFileChange = (index, event) => {
    const updatedImages = [...images];
    updatedImages[index] = event.target.files[0];
    setImages(updatedImages);
  };

  const handleRemove = (index) => {
    const updatedImages = [...images];
    updatedImages[index] = null; // Clear specific image slot
    setImages(updatedImages);
  };

  const handleStickerDragStart = (sticker) => (event) => {
    event.dataTransfer.setData('sticker', sticker);
  };

  const handleDrop = (index) => (event) => {
    event.preventDefault();
    const sticker = event.dataTransfer.getData('sticker');
    setStickers((prevStickers) => [
      ...prevStickers,
      { sticker, imageIndex: index, position: { x: event.clientX, y: event.clientY } },
    ]);
  };

  const saveFrame = () => {
    const frame = {
      images,
      stickers,
      description,
    };
    setSavedFrames([...savedFrames, frame]);
    alert('Frame saved!');
  };

  return (
    <div>
      {/* Dropdown for Number of Images */}
      <div style={{ marginBottom: '20px' }}>
        <label>
          Select number of images:
          <select
            value={numImages}
            onChange={handleNumImagesChange}
            style={{
              marginLeft: '10px',
              padding: '5px',
              fontSize: '14px',
              borderRadius: '5px',
            }}
          >
            {[1, 2, 3, 4].map((num) => (
              <option key={num} value={num}>
                {num}
              </option>
            ))}
          </select>
        </label>
      </div>

      {/* Sticker Options */}
      <div style={{ marginBottom: '20px', display: 'flex', gap: '10px' }}>
        {['â­', 'â¤ï¸', 'ðŸŽ‰', 'ðŸŒŸ'].map((sticker) => (
          <button
            key={sticker}
            draggable
            onDragStart={handleStickerDragStart(sticker)}
            style={{
              fontSize: '20px',
              padding: '10px',
              cursor: 'grab',
              border: '1px solid gray',
              borderRadius: '5px',
            }}
          >
            {sticker}
          </button>
        ))}
      </div>

      {/* Frame with Dynamic Grid */}
      <div
        style={{
          border: '2px solid black', // Border for the frame
          borderRadius: '10px',
          padding: '10px',
          marginBottom: '20px',
          display: 'inline-block',
        }}
      >
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: `repeat(${Math.min(2, numImages)}, 1fr)`,
            gap: '10px',
          }}
        >
          {Array.from({ length: numImages }).map((_, index) => (
            <div
              key={index}
              style={{
                position: 'relative',
                textAlign: 'center',
                width: '150px',
                height: '150px',
                border: '1px dashed gray',
                backgroundColor: '#f8f8f8', // Light background for better visibility
              }}
              onDragOver={(e) => e.preventDefault()}
              onDrop={handleDrop(index)}
            >
              {images[index] ? (
                <>
                  <img
                    src={URL.createObjectURL(images[index])}
                    alt="Preview"
                    style={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'cover',
                    }}
                  />
                  <button
                    onClick={() => handleRemove(index)}
                    style={{
                      position: 'absolute',
                      top: 0,
                      right: 0,
                      backgroundColor: 'red',
                      color: 'white',
                      border: 'none',
                      borderRadius: '50%',
                      width: '20px',
                      height: '20px',
                      cursor: 'pointer',
                    }}
                  >
                    &times;
                  </button>
                </>
              ) : (
                <label>
                  <div
                    style={{
                      width: '100%',
                      height: '100%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      cursor: 'pointer',
                    }}
                  >
                    +
                  </div>
                  <input
                    type="file"
                    style={{ display: 'none' }}
                    onChange={(e) => handleFileChange(index, e)}
                  />
                </label>
              )}

              {/* Render Stickers */}
              {stickers
                .filter((sticker) => sticker.imageIndex === index)
                .map((sticker, i) => (
                  <div
                    key={i}
                    style={{
                      position: 'absolute',
                      top: `${sticker.position.y}px`,
                      left: `${sticker.position.x}px`,
                      fontSize: '20px',
                    }}
                  >
                    {sticker.sticker}
                  </div>
                ))}
            </div>
          ))}
        </div>
      </div>

      {/* Text Input Section */}
      <div style={{ marginTop: '20px' }}>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Enter a description here..."
          style={{
            width: '100%',
            height: '80px',
            padding: '10px',
            fontSize: '14px',
            border: '1px solid gray',
            borderRadius: '5px',
          }}
        ></textarea>
      </div>

      {/* Save Frame Button */}
      <button
        onClick={saveFrame}
        style={{
          marginTop: '20px',
          padding: '10px 20px',
          backgroundColor: 'green',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        Save Frame
      </button>
    </div>
  );
}

export default ImageStickerUploader;



